mapper.update:
update cost_subject
set text = #{text,jdbcType=VARCHAR},
  parent_id = #{parentId,jdbcType=INTEGER},
  leaf = #{leaf,jdbcType=SMALLINT},
  deleted = #{deleted,jdbcType=SMALLINT}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, text, parent_id, leaf, deleted
from cost_subject
where id = #{id,jdbcType=INTEGER}
